import React,  { useState, forwardRef, useImperativeHandle } from 'react';
import axios from "axios";
import './App.css';
import { render } from '@testing-library/react';
//import GetSettings from "./getSettings.js"


const GetMetadata = forwardRef((props, ref)=>{

    const [renderArea, setRenderArea] = useState(false)
    useImperativeHandle(ref, ()=>({
        getProcesses(){
            axios.get("/submitSettings")
            .then(function(response){
                console.log(response)
                console.log(response.data[0])
                console.log(response.data[1])

                //Parse values from response data to boolean format
                for (let prop in response.data[0]) {
                    if (response.data[0].hasOwnProperty(prop)) {
                        response.data[0][prop] = (response.data[0][prop] == 'true' || response.data[0][prop] == 'false')? response.data[0][prop] === 'true': response.data[0][prop] ;
                    }
                } 
                //Checkbox to Compute marker positive areas to all markers
                //+ input fields to custom markers
                if(response.data[0].execute_area == true){
                    console.log("RENDER SOMETHING")
                    setRenderArea(true)
                    //console.log("Render area ", renderArea)

                }
                else if(response.data[0].execute_area == false)setRenderArea(false)

                if(response.data[0].execute_sd_segmentation == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_cell_type_identification == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_cell_clustering == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_cell_thresholding == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_homotypic_interactions == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_heterotypic_interactions == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_permuted_interactions == true){
                    console.log("RENDER SOMETHING")
                }

                //console.log(skip_area)
                //console.log(response.data.skip_area)
            })
        },
        submitMetadata(){

            console.log("metadata")


            console.log("getMETADATA")
        }
    }))

    return(
        <div>
            <form class="metadataForm">
                <b>Submit metadata:</b>
                {renderArea} (
                    <p>AREA</p>
                ):(
                    <></>
                )}



            </form>
        </div>

        )

})
export default GetMetadata;
