import React,  { useState, forwardRef, useImperativeHandle } from 'react';
import axios from "axios";
import './App.css';
import { toBeRequired } from '@testing-library/jest-dom/dist/matchers';

const GetResults = () => { 

    const [plotPaths, setPlotPaths] = useState("")
    const [areaPlots, setAreaPlots] = useState("")
    const [trigger, setTrigger] = useState(true)

    /*if(trigger === true){
        displayResults()
        setTrigger(false)
    }*/

    const displayResults = () => {
        //document.getElementById("resultsButton").style.display = "none"
        console.log("OK")
        axios.get("/fetchResults")
        .then(function(response){
           //let paths = "../src/images/area/total_ROI_area_boxplots.png"
           let paths = response.data
           setPlotPaths(paths)
           displayImages(paths)          
        })
    }

    const displayImages = (paths) => { 
        console.log(paths)
        //console.log(typeof "../src/images/area/total_ROI_area_boxplots.png")
        //setAreaPlots(<img src={require("../src/images/area/total_ROI_area_boxplots.png")}/>)
        //paths = "/home/ernohanninen/simpli-app/src/images/area/total_ROI_area_boxplots.png"
        //let path = "area/total_ROI_area_boxplots.png"
        //console.log(path)
        console.log("../src/images/" + paths)
        setAreaPlots(<img class = "image" src={require("../src/images/" + paths)}></img>)



    } 

    return(
    <div>
      <div hidden id="load">
          <p>Running the analysis.</p>         
      </div>

      <div hidden id="results">        
          <button id="resultsButton" onClick={event => displayResults()}>Display results</button>
          {areaPlots}
          <a href="http://localhost:3000">Home page</a>         
      </div>
    </div>

    )
}
export default GetResults;
