import React,  { useState, forwardRef, useImperativeHandle } from 'react';
import axios from "axios";
import './App.css';
import { v4 as uuidv4 } from 'uuid';

import { render, waitForElementToBeRemoved } from '@testing-library/react';
//import GetSettings from "./getSettings.js"



//If no markers prevent user to go forward add new textbox
//able user input non unique markers
//adding custom markers doesn't work
//Add random color generator for mask input
//Using boolean it should be possible to check wether the input is filled before adding more mask metadata
//If there is no marker the axios posts still sends data
//Even if the area is unchecked the axios posts the data that is submitted on the main page (CHECK THAT THIS IS FIXED)

//Check that the colors for cell_masking_metadata are okay

const GetMetadata = forwardRef((props, ref)=>{

    const [renderArea, setRenderArea] = useState(false)
    const [renderMasking, setRenderMasking] = useState(false)
    const [allMarkers, setAllMarkes]  = useState(false)
   // const [inputFields, setInputFields] = useState([]);
    const [markersDict, setMarkersDict] = useState([{}])
    const [counter, setCounter] = useState(0)
    const [maskCounter, setMaskCounter] = useState(0)
    const [maskKeyCounter, setMaskKeyCounter] = useState(0)
    const [newMask, setNewMask] = useState(false)
    let maskTemp = ({cellType:"", thresholdMarker:"", thresholdValue:"", color:""})
    let inputFields = []


    const [originalMarkers, setOriginalMarkers] = useState([]) //This list keeps in track about the markers that the user inputted
    const [markerTextBox, setMarkerTextBox] = useState("")
    const [maskingTextBox, setMaskingTextBox] = useState("")
    const [maskDict, setMaskDict] = useState([{}])
    const [errorMessageArea, setErrorMessageArea] = useState("")

    useImperativeHandle(ref, ()=>({
        getProcesses(){
            console.log("AXIOS")
            axios.get("/submitSettings")
            .then(function(response){

                //setMarkersDict(response.data[1])
                //setMarkers(response.data[2])
                //a list of markers


                //console.log("Render area ", renderArea)

                //Parse values from response data to boolean format
                for (let prop in response.data[0]) {
                    if (response.data[0].hasOwnProperty(prop)) {
                        response.data[0][prop] = (response.data[0][prop] == 'true' || response.data[0][prop] == 'false')? response.data[0][prop] === 'true': response.data[0][prop] ;
                    }
                } 
                //Checkbox to Compute marker positive areas to all markers
                //+ input fields to custom markers
                if(response.data[0].execute_area == true){
                    console.log("EXECUTE AREA")                   
                    setRenderArea(true)
                    setMarkerTextBox("")
                    let markers = response.data[1] //dictionary of markers
                    allMarkersChecked(markers)
                    setOriginalMarkers(response.data[2])

                }
                else if(response.data[0].execute_area == false)setRenderArea(false)

                if(response.data[0].execute_sd_segmentation == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_cell_type_identification == true){
                    setRenderMasking(true)
                    maskingInput()
                    console.log("RENDER SOMETHING")
                }
                else if(response.data[0].execute_cell_type_identification == false){
                    setRenderMasking(false)
                    setMaskingTextBox("")

                }
                if(response.data[0].execute_cell_clustering == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_cell_thresholding == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_homotypic_interactions == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_heterotypic_interactions == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_permuted_interactions == true){
                    console.log("RENDER SOMETHING")
                }

                //console.log(skip_area)
                //console.log(response.data.skip_area)
            })
        },
        submitMetadata(){
            console.log("SUBMIT METADATA")

            const url = '/submitMetadata';
            axios.post(url, {
                "markers": markersDict,
                "masks": maskDict
                })
                .then((response) =>{
                    if(response.data=="error"){ //If error
                        console.log("########ERROR########")
                    }
                    else{ //IF everyting okay
                        console.log("READY")
                    }
            });      
        }
    }))

    const allMarkersChecked = (markers) => {    
            console.log("ORIGINAL MARKERS", markers)
            setMarkersDict(markers)
            let count = -1;

            //let markers = originalMarkers //Using a temp list because the state variable doesn't update immediately 
            console.log(markers)
            setMarkerTextBox(Object.entries(markers).map(([key,index]) => {
                let obj = markers[key]
                console.log(obj)
                let marker = Object.keys(obj)[0]
                console.log(marker)
                //if(marker in originalMarkers){//THis doesn't work because the marker is located in 0:{marker:mainMarker}
                    console.log("INCLUDES")
                    count = count + 1
                    setCounter(count)  

                    console.log(count)
                    return(<div class="row" id={count} value={marker}>
                        <div class="col-1">    
                            <input name="marker" value={marker} disabled size="10"/>                
                        </div>
                        <div class="col-2">    
                            <input name="mainMarker" value={marker} disabled size="10"/>                
                        </div>
                        <div class="col-3">
                            <button  onClick={() => removeMarker(count)}>Remove</button>                  
                        </div>  
                    </div>)        
            }
        ))             
    }

    const addMarkers = event =>{
        let count = counter + 1
        console.log(count)
        setMarkerTextBox([...markerTextBox,
        <div class="row" id={count}>
            <div class="col-1">    
                <input name="marker"  id={0-count} onBlur={event => handleChangeMarker(count,event)} size="10"/>                
            </div>
            <div class="col-2">    
                <input name="mainMarker" onBlur={event => handleChangeMainMarker(count,event)} size="10"/>                
            </div>
            <div class="col-3">
                <button disabled={markerTextBox.length === 0} onClick={() => removeMarker(count)}>Remove</button>        
            </div> 

        </div>
        ])  
        setCounter(count)       
    }


    const removeMarker = (id) => {
        let key = document.getElementById(id).getAttribute("value")
        delete markersDict[key]
        document.getElementById(id).remove()
    }

    const handleChangeMarker = (id, event) => {
        let inputMarkers = []

        let inputString = event.target.value.replace(/\s/g, "") //Removes the whitespaces
        let marker = inputString
        //Checks the logical operators the user inputted
        if(marker.includes("&") || marker.includes("|") || marker.includes("!")){
            inputMarkers = marker.split(/[&!|]+/) //Makes a list of the markers
        }
        else{
            //If the input didn't contain logical operators -> there was only one marker, hence we can put it directly to a list
            inputMarkers.push(marker)
        }
        let inputError = false
        //Loop that checks the user inputted a marker that that the tiff-images have
        for(let elem in inputMarkers){
            if(originalMarkers.includes(inputMarkers[elem])){
                inputError = false
            }
            else{
                inputError = true
                break;
            }
        }

        let dict = markersDict
        console.log(inputError)
        //If the tiff-images have the marker we can put it to the dictionary
        if(inputError === false){
            //setMarkersDict({...markersDict,[inputString]:""})
            console.log("SET MARKER TO DICT")
            console.log("MARKERSDICT", markersDict)
            let update = false
            setMarkersDict(Object.entries(markersDict).map(([item]) => {
                console.log("ITEM:",item)
                if(item == id){
                    update = true
                    console.log("UPDATE")
                    console.log("ERISUURI, ", inputString)
                    console.log(markersDict)
                    //return([...markersDict, id])
                    return {...markersDict,[id]:{[inputString]:""}}
                }

            }))
            if(update === false){
                console.log("update is false, ID : ", id)
                setMarkersDict({...markersDict,[id]:{[inputString]:""}})
            }
            /*setMarkersDict(markersDict.map(function(item, i){
                console.log(item)
                return{...markersDict,inputString}
            })*/

            }
        //If not, raising an error to the user
        else if(inputError === true){
            setErrorMessageArea("ERROR: input " + inputString + " couldn't be processed, please change the input")
            //alert("ERROR: input " + inputString + " couldn't be processed, please change the input format")
        }

      }

    const handleChangeMainMarker = (id,event) => {
        console.log("MARKERSDICT", markersDict)
        console.log("Markers Dict type", typeof(markersDict))
        let mainMarker = event.target.value.replace(/\s/g, "")
        let key = document.getElementById(0-id).value
        let inputError = handleMarkerInput(mainMarker)
        //If the tiff-images have the marker we can put it to the dictionary
        if(inputError === false){
            console.log("SET MARKER TO DICT")
            console.log("MARKERSDICT", markersDict)
            let update = false
            setMarkersDict(Object.entries(markersDict).map(([item]) => {
                console.log("MAINMARKER ITEM ", item)
                if(item != id){
                    update = true
                    console.log("UPDATE")             
                }

            }))
            if(update === false){
                console.log("update is false, ID : ", id)
                setMarkersDict({...markersDict,[id]:{[key]:mainMarker}})
            }        
        }
        //If not, raising an error to the user
        else if(inputError === true){
            setErrorMessageArea("ERROR: input " + mainMarker + " couldn't be processed, please change the input")
            //alert("ERROR: input " + mainMarker + " couldn't be processed, please change the input format")
        }   
    }

    function handleMarkerInput(marker){
        let inputMarkers = []
        console.log("INPUMarker", marker)
        console.log("MARKERSDICT",markersDict)
        //Checks the logical operators the user inputted
        if(marker.includes("&") || marker.includes("|") || marker.includes("!")){
            inputMarkers = marker.split(/[&!|]+/) //Makes a list of the markers
        }
        else{
            //If the input didn't contain logical operators -> there was only one marker, hence we can put it directly to a list
            inputMarkers.push(marker)
        }
        let inputError = false
        //Loop that checks the user inputted a marker that that the tiff-images have
        for(let elem in inputMarkers){
            if(originalMarkers.includes(inputMarkers[elem])){
                inputError = false
            }
            else{
                inputError = true
                break;
            }
        }
        return inputError
    }

    const maskingInput = () => {    
        console.log("MASKING")
        let id = "mask0" 
        //If you go back to previous page, this code mess up
        if(maskingTextBox === ""){
            setMaskingTextBox(Object.entries(maskingTextBox+1).map(([key,index]) => {

                return(
                    <div class="row" id={id}>
                        <div class="col-1">    
                            <input name = "mask0" onBlur={event => handleChangeMask(id,event, "cellType")}  size="10"/>                
                        </div>
                        <div class="col-2">    
                            <input name = "mask0" onBlur={event => handleChangeMask(id,event, "thresholdMarker")} size="10"/>                
                        </div>
                        <div class="col-3">    
                            <input name = "mask0"  onBlur={event => handleChangeMask(id,event, "thresholdValue")} size="10"/>                
                        </div>
                        <div class="col-4">    
                            <input name = "mask0"  onBlur={event => handleChangeMask(id,event, "color")}  size="10"/>                
                        </div>
                        <div class="col-5">
                            <button onClick={() => removeMask(id, maskKeyCounter)}>Remove</button>        
                        </div> 
                    </div>)

            }))
        }

    }

    const addMasks = event =>{


        if(Object.values(maskTemp).filter(x => x === "").length === 0){
            alert("Fill the form")
        }
        //This is a bit in a wrong place, the last row is never disabled
        //Disables element after new one is added
        if(Object.keys(maskDict).includes(maskCounter.toString())){
            let name = "mask" + maskCounter
            document.getElementsByName(name).forEach(e => {
                e.disabled = true
            })        
        }
        let count = maskCounter + 1
        let id = "mask" + count
        setMaskCounter(count)

        setMaskingTextBox([...maskingTextBox,
        <div class="row" id={id} >
            <div class="col-1">    
                <input name={id}  onBlur={event => handleChangeMask(id,event, "cellType")} size="10"/>                
            </div>
            <div class="col-2">    
                <input name={id} disabled={Object.keys(maskDict).includes(maskKeyCounter-1)} onBlur={event => handleChangeMask(id,event, "thresholdMarker")} size="10"/>                
            </div>
            <div class="col-3">    
                <input name={id} disabled={Object.keys(maskDict).includes(maskKeyCounter-1)} onBlur={event => handleChangeMask(id,event, "thresholdValue")} size="10"/>                
            </div>
            <div class="col-4">    
                <input name={id} disabled={Object.keys(maskDict).includes(maskKeyCounter-1)} onBlur={event => handleChangeMask(id,event, "color")} size="10"/>                
            </div>
            <div class="col-5">
                <button onClick={() => removeMask(id, maskKeyCounter)}>Remove</button>        
            </div> 

        </div>
        ])  
    }

    const handleChangeMask = (id,event, input) => {
        console.log("MASK LENGTH : ", Object.values(maskTemp).filter(x => x === "").length)
        setMaskKeyCounter(maskKeyCounter + 1)
        //Checks what is the input of the function and stores the input to dictionary to right location
        if(input === "cellType"){
            maskTemp = {...maskTemp, cellType:event.target.value}  
        }
        else if(input === "thresholdMarker"){
            maskTemp = {...maskTemp, thresholdMarker:event.target.value}
        }
        else if(input === "thresholdValue"){
            maskTemp ={...maskTemp, thresholdValue:event.target.value} 
        }
        else if(input === "color"){
            maskTemp ={...maskTemp, color:event.target.value}
        }
        //After the temp dictionary is filled store the properties to the main dictionary
        if(Object.values(maskTemp).filter(x => x === "").length === 0){
            setMaskDict({...maskDict,[maskKeyCounter]:[{cell_type:maskTemp["cellType"]},{ threshold_marker:maskTemp["thresholdMarker"]}, {threshold_value:maskTemp["thresholdValue"]}, {color:maskTemp["color"]}]})     
        }    
    }



    //Removes the mask metadata input field and the corresponding property from the dictionary
    const removeMask = (id,dictKey) => {
        delete maskDict[dictKey]
        document.getElementById(id).remove()
    }

    return(
        <div>
            <form class="metadataForm">
                <b>Submit metadata:</b>
                {renderArea ? (
                    <div>
                    <React.Fragment> 
                        <p>Area</p>
                        <div class="row">
                                <div class="col-1">    
                                    <p>Marker</p>
                                </div>
                                <div class="col-2">    
                                    <p>Main marker</p>
                            </div>
                        </div>
                        {markerTextBox}
                        <button type="button" onClick={addMarkers}>Add markers</button>

                    </React.Fragment>
                    </div>                  
                ):(<></>)} 
                <br></br>
                {renderMasking ? (
                    <div>
                    <React.Fragment> 
                        <p>Masking</p>
                        <div class="row">
                                <div class="col-1">    
                                    <p>Cell type</p>
                                </div>
                                <div class="col-2">    
                                    <p>Threshold marker</p>
                                </div>
                                <div class="col-3">    
                                    <p>Threshold value</p>
                                </div>
                                <div class="col-4">    
                                    <p>Color</p>
                                </div>             
                            </div>
                        {maskingTextBox}
                        <button type="button" onClick={addMasks}>Add more samples</button>
                    </React.Fragment>
                    </div>                  
                ):(<></>)}     

            </form>
        </div>
        ) 
})
export default GetMetadata;
//<div>{allMarkers && <p>Loading...</p>}</div>#setAllMarkes(!allMarkers);
