import React,  { useState, forwardRef, useImperativeHandle } from 'react';
import axios from "axios";
import './App.css';

function GetResults() {

    const [plotPaths, setPlotPaths] = useState("")
    const [areaPlots, setAreaPlots] = useState("")
    const [trigger, setTrigger] = useState(true)

    if(trigger === true){
        displayResults()
        setTrigger(false)
    }


   function displayResults(){
        console.log("OK")
        axios.get("/submitSettings")
        .then(function(response){
           console.log(response.data[0])
           let paths = response[0]
           setPlotPaths(paths)
           displayImages(paths)          
        })
    }

    const displayImages = (paths) => { 
        console.log(paths)
        setAreaPlots(<img src={require("../src/images/area/total_ROI_area_boxplots.png")}/>)
    } 

    return(
    <div>
      <div hidden id="load">
          <p>Running the analysis.</p>         
      </div>
      <React.Fragment> 
      <div hidden id="results">
          <p>Display results</p>
          {areaPlots}
          {console.log("AREA PLOTS ", areaPlots)}
          <a href="http://localhost:3000">Home page</a>         
      </div>
      </React.Fragment> 
    </div>

    )
}
export default GetResults;
 //      <App callparentFunction={()=>handleSettingsSubmit}/>
