import React,  { useState, forwardRef, useImperativeHandle } from 'react';
import axios from "axios";
import './App.css';
//import { toBeRequired } from '@testing-library/jest-dom/dist/matchers';

const GetResults = () => { 

    const [plotPaths, setPlotPaths] = useState("")
    const [areaPlots, setAreaPlots] = useState("")
    const [sampleImages, setSampleImages] = useState("")
    const [trigger, setTrigger] = useState(true)

    /*if(trigger === true){
        displayResults()
        setTrigger(false)
    }*/

    const displayResults = () => {
        //document.getElementById("resultsButton").style.display = "none"
        console.log("OK")
        axios.get("/fetchResults")
        .then(function(response){
           //let paths = "../src/images/area/total_ROI_area_boxplots.png"
           let data = response.data
           let samples = data["samples"]

           console.log(samples)
           let area = data["area"]
           console.log(area)
           setPlotPaths(area)
           displayImages(area, samples)          
        })
    }

    const displayImages = (area, samples) => { 
        console.log(area)

        //Map samples
        Object.entries(samples).map(([key,value]) => {
                console.log(key)
                console.log(value["preprocessed"])

                console.log(value["thresholded"])
                console.log("_______________")
        })

        setAreaPlots(<img src={require("../src/images/" + area)}></img>)
        //setAreaPlots(<img src={require("../src/images/logo.svg")}></img>)



    } 

    return(
    <div>
    <form id="resultsForm">

      <div hidden id="load">
          <p>Running the analysis.</p>         
      </div>

      <div hidden id="results">        
          <button id="resultsButton" type="button" onClick={event => displayResults()}>Display results</button>
          {areaPlots}
      </div>
      </form>
    </div>

    )
}
export default GetResults;
