//created 15. may
import React, { useState, useRef } from 'react';
//import * as React from 'react';
import './App.css';
import axios from "axios";
//import React from "react";
import { v4 as uuidv4 } from 'uuid';
import Table from "./sample_table.js";
//import handleSettingsSubmit from "./getSettings.js";
import GetMetadata from "./getMetadata.js"
import GetSettings from "./getSettings.js"
import { AiOutlineInfoCircle } from 'react-icons/ai';

//const App = (props) => {
function App(props){

  const [submitting, setSubmitting] = useState(false);
  const [sample, setSample] = useState("");
  const [color, setColor] = useState("");
  const [comparison, setComparison] = useState("");
  const [marker, setMarker] = useState("")
  const [inputFields, setInputFields] = useState([
    { id: uuidv4(), tiff: '', marker: '', label:''}, ]);
  const [tableData, setTableData] = useState([])
  const [isHovering1, setIsHovering1] = useState(false)
  const [isHovering2, setIsHovering2] = useState(false)
  const [info, setInfo] = useState("")
  //const [sample_list, setSample_list] = useState([])
  const sample_list = []

  const handleSubmit = event => {

    //console.log(table.values())

    event.preventDefault();    
    const markers = []
    const labels = []
    for(let i=0;i<inputFields.length;i++){
        markers[i] = inputFields[i]["marker"]
        labels[i] = inputFields[i]["label"]
    }
    let empty = true
    if (tableData.length==0){ empty = true }
    else{ empty = false }
    /*console.log(sample_list)
    console.log(typeof(sample_list))

    //setSample_list(sample);
    sample_list.push(sample)
    console.log(sample_list)*/


    const formData = new FormData()
    formData.append("table_state", empty)
    formData.append("sample", sample)
    formData.append("color", color)
    formData.append("comparison", comparison)
    for(let i = 0; i<inputFields.length;i++){
      formData.append("tiffs", inputFields[i]["tiff"])
    }
    formData.append("marker", markers)
    formData.append("labels", labels)    
    const newData = []
    const tiffs = []
    let j = 0
    for (const pair of formData.entries()) {
      if(typeof(pair[1])=="object"){    
        tiffs[j] = (pair[1].name)
        j++
      }
      else{ newData[pair[0]] = pair[1] }

    }
    newData["tiffs"] = tiffs

    //Display the form data in html table
    const d = (data)=>([...data,newData])
    setTableData(d)

    console.log(tableData[0])


    //Send formData to backend 
    const url = '/sampleMetadata';
    axios.post(url, formData).then((response) =>{
      if(response.data=="error"){ //If error
        console.log("########ERROR########")
      }
      else{ //IF everyting okay
        console.log("READY")
      }
    });

    event.target.reset()//Empty variables
    //Removes the dynamically created "add new tiff" elements
      inputFields.map(i => {
        const values  = inputFields
        values.splice(values.findIndex(value => value.id === i.id), inputFields.length-1);       
        setInputFields(values);
      })
    setInputFields([
      { id: uuidv4(), tiff: '', marker: '', label:''}, ])
    setSubmitting(true);
    setTimeout(() => {
      setSubmitting(false);
    }, 1);
  }

  const handleChangeInput = (id, event) => {
    const newInputFields = inputFields.map(i => {
      if(id === i.id) {
        console.log(event.target.name)
        if(event.target.name == "tiff"){
          //console.log(event.target.files[0])
          //console.log(event.target.name)
          i[event.target.name] = event.target.files[0]
        }
        else{
          i[event.target.name] = event.target.value
          if(event.target.name == "marker"){
            i["label"] = event.target.value
            console.log("OK")
          }
        }
      }      
      return i;
    })   
    setInputFields(newInputFields);  
  }

  const addNewTiff = event =>{
    setMarker("")
    setInputFields([...inputFields, { id: uuidv4(),  tiff: '', marker: '' }])
  }

  const handleRemoveFields = id => {
    const values  = [...inputFields];
    values.splice(values.findIndex(value => value.id === id), 1);
    setInputFields(values);
  }



  const settingsPage = event => {
    console.log("_______________")
    //event.preventDefault();
    document.getElementById("samplePage").style.display= "none";
    document.getElementById("nextButton1").style.display = "none"
    document.getElementById("settings").style.display = "block";
    document.getElementById("returnButton").style.display = "block";
    document.getElementById("nextButton2").style.display = "block"     
  }

  const samplePage = event => {
    //event.preventDefault(); 
    document.getElementById("samplePage").style.display= "block";
    document.getElementById("returnButton").style.display = "none";
    document.getElementById("settings").style.display = "none";
    document.getElementById("nextButton1").style.display = "block"
    document.getElementById("nextButton2").style.display = "none"



  }

  const returnSettingsPage = event => {
    //event.preventDefault(); 
    document.getElementById("settingsPage").style.display= "block";
    document.getElementById("nextButton2").style.display = "block";
    document.getElementById("returnButton").style.display = "block"
    document.getElementById("returnButton2").style.display = "none"
    document.getElementById("nextButton3").style.display = "none"


  }

  const showInstructions = event => {
    //setInfo()
    document.getElementById("instructions").style.display="block"
    document.getElementById("infoBtn").style.display="none"
    document.getElementById("hideInfoBtn").style.display="block"


  }

  const hideInstructions = event =>{
    document.getElementById("instructions").style.display="none"
    document.getElementById("infoBtn").style.display="block"
    document.getElementById("hideInfoBtn").style.display="none"
  }



  /*const sampleHandleMouseOver = () => {

    setIsHovering1(true)
    setInfo("Identifier to be used to refer to this sample in the analysis")

  }

  const colorHandleMouseOver= () => {
    setIsHovering1(true)
    setInfo("Color used to represent this sample in plots.")
  }
  const comparisonHandleMouseOver = () => {
    setIsHovering1(true)
    setInfo('Pairwise comparisons will be made only if there are two category names among samples. To exclude a sample from comparison, set comparsion field to NA.')
  }
  const tiffHandleMouseOver = () => {
    setIsHovering2(true)
    setInfo("Several single-channel TIFF files can be submitted for each sample")
  }

  const markerHandleMouseOver = () => { 
    setIsHovering2(true)
    setInfo("Marker associated to the channel.")   
  }

  const labelHandleMouseOver = () => {  
    setIsHovering2(true)
    setInfo('Label used to name the channel in the analysis. By default the value from marker textbox is copied.')  
  }



  const handleMouseOut = () => {
    setIsHovering1(false)
    setIsHovering2(false)
    setInfo("")
  }*/

  const settingsChildRef = useRef();
  const metadataChildRef = useRef();

  return (
    <div>
  <div id="samplePage">

      <form  onSubmit={handleSubmit}>
        <b>Submit samples:</b>

          <div class="row">
            <div class="col-1">  

                  <p name = "sample">Sample</p>
                  <input name="sample" onChange={(e)=>setSample(e.target.value)}/>                
            </div>

            <div class="col-2">    

                  <p>Color</p>
                  <input name="color" onChange={(e)=>setColor(e.target.value)}/>
            </div>
            <div class="col-3">    

                  <p>Comparison</p>
                  <input name="comparison" onChange={(e)=>setComparison(e.target.value)}/>


            </div>

          </div>

            { inputFields.map(inputField => (


          <div key={inputField.id}>
           <div class="row">
            <div class="col-1">    
                  <p>Load tiff</p>
                  <input name="tiff" type = "file" onChange={event => handleChangeInput(inputField.id,event)}/>                

            </div>
            <div class="col-2">    


                <p>Marker</p>
                <select name="marker" id="marker" onChange={event => handleChangeInput(inputField.id,event)}>
                    <option selected="true" disabled="disabled">Select</option>    
                    <option value="HuNu">HuNu</option>
                    <option value="TH">TH</option>
                    <option value="OREXIN">OREXIN</option>
                </select>

            </div>
            <div class="col-3">    
                  <p>Label</p>
                  <input name="label" value={inputField["label"]} onChange={event => handleChangeInput(inputField.id,event)} size="10"/>                

            </div>
            <div class="col-remove">
              <button disabled={inputFields.length === 1} onClick={() => handleRemoveFields(inputField.id)}>Remove</button>

            </div>    

          </div>
          </div>

          )) }
          <br></br>

          <div class="row">
            <div class="col-1">  
              <button type="button" onClick={addNewTiff}>Add new tiff</button>
            </div>
            <div class="col-2">  

              <button type="submit">Submit sample</button>
            </div>   
            <div >  

            <div id = "infoBtn" class="btn-instructions">  
                <button type="button" onClick={showInstructions}>Instructions</button>
          </div>  
          <div  hidden id = "hideInfoBtn" class="btn-instructions">  
                <button type="button" onClick={hideInstructions}>Hide Instructions</button>
          </div>  
          </div>    

          </div>

          <div hidden id="instructions" class="textarea-container">    
              <p disabled readonly rows="20" cols="50">
                <b>Instructions:<br></br></b>
                <b>Sample:</b> Identifier to be used to refer to this sample in the analysis. 
                <br></br><b>Color</b>: Color used to represent this sample in plots.
                <br></br><b>Comparison</b>: Pairwise comparisons will be made only if there are two category names  <br></br>among samples. To exclude a sample from comparison, set comparsion field to NA.
                <br></br><b>Load tiff:</b> Several single-channel TIFF files can be submitted for each sample.
              </p>        
        </div>
          <div class="col-1">  

        <Table tableData={tableData} id="sampleTable" class="sampleTable"/>
        </div>


        <br></br>

      </form>




  </div>


  <div hidden id="settings" >
    <GetSettings ref={settingsChildRef}/>
    <br></br>
  </div>

  <div hidden id="metadata">


    <GetMetadata ref={metadataChildRef}/>
    <br></br>
  </div>

  <div class ="row" >

          <div class ="next-btn" id="nextButton1">

            <button disabled={tableData.length === 0} type="button" onClick={settingsPage}>Next</button>

          </div>

          <div hidden class ="next-btn" id="nextButton2">
            <button type="button" onClick={()=>settingsChildRef.current.submitSettings()}>Next2</button>

          </div>
          <div hidden class ="next-btn" id="nextButton3">
            <button type="button" onClick={()=>metadataChildRef.current.submitMetadata()}>Next3</button>

          </div>

          <div hidden class ="return-btn" id="returnButton">

            <button type="button" onClick={samplePage}>Return</button>

          </div>
          <div hidden class ="return-btn" id="returnButton2">

            <button type="button" onClick={returnSettingsPage}>Return2</button>

          </div>

        </div>



</div>


  );
}

export default App;

