import React,  { useState, forwardRef, useImperativeHandle } from 'react';

//import { MDBTable, MDBTableHead, MDBTableBody } from 'mdb-react-ui-kit';
//function Table({tableData}){
const Table = (({tableData, setTableData}) => {

    const deleteRow = (index, e) =>{
        console.log("NYT VITTU")
        console.log(index)
        setTableData(tableData.filter((v, i) => i !== index));
        //solve this in app .js file, pass the index


        //var i = event.parentNode.parentNode.rowIndex;
        //document.getElementById("table").deleteRow(i);
    }
    return(

<table striped bordered hover class="sampleTable" id ="table">
            <thead>
                <tr>
                    <id>Id</id>
                    <th>Sample</th>
                    <th>Color</th>
                    <th>Comparison</th>
                    <th>Tiff files</th>
                    <th>Marker</th>
                    <th>Label</th>

                </tr>
            </thead>
            <tbody>
            {
                tableData.map((data, index)=>{

                    return(
                        <tr key={index}>
                            <td>{index+1}</td>
                            <td>{data.sample}</td>
                            <td>{data.color}</td>
                            <td>{data.comparison}</td>
                            <td>{data.tiffs}</td>
                            <td>{data.marker}</td>
                            <td>{data.labels}</td>

                            <td><button type="button" onClick={(e)=>deleteRow(index,e)}>Delete</button></td>


                        </tr>
                    )
                })
            }
            </tbody>
        </table>
    )
})
export default Table;
