import React,  { useState, forwardRef, useImperativeHandle } from 'react';
import axios from "axios";
import './App.css';


const GetResults = () => { 

    //Variables
    const [plotPaths, setPlotPaths] = useState("")
    const [areaPlots, setAreaPlots] = useState("")
    const [sampleImages, setSampleImages] = useState("")
    const [dropDown, setDropDown] = useState("")
    var sample = ""

    //Function which get the images from backend
    const displayResults = () => {
        document.getElementById("resultsButton").style.display = "none"
        axios.get("/fetchResults")
        .then(function(response){
           let data = response.data //Response value is a dictionary
           //Extracting values from dictionary
           let samples = data["samples"]
           let area = data["area"]
           console.log(area)
           console.log(samples)

           setPlotPaths(area)    
           displayImages(area, samples) //Calling function which displays the images         
        })
    }
    //Function which displays the images to the user
    const displayImages = (area, samples) => { 
        //Get the values for drop-down list
        let optionItems = Object.entries(samples).map(([key,value]) => 
            <option value={key}>{key}</option>
        )
        //Create the dropdown list
        setDropDown(
        <div class="row">
            <p>Quality control: </p>
            <select name="sampleSelector" id="sampleSelector" onChange={event => handleChangeSelect(event)}>
                 <option selected="true">None</option>  
                 {optionItems}

            </select>
        </div>
        )
        let displaySegmented = false

        console.log("Object length : ", Object.keys(samples))
        if(Object.keys(samples).length === 3){
            displaySegmented = true
        }
        console.log(displaySegmented)



        //Map samples and set the images to variable
        setSampleImages(Object.entries(samples).map(([key,value]) => {
            let idThresholded = "idThres" +key
            let idPreprocessed = "idPre" +key
            let idSegmented = "idSeg" +key
            return([...sampleImages, 
            <div id = {key} hidden>
                <div>
                    <p>Preprocessed {key}</p>    
                    <img class="contain" id ={idPreprocessed} src={require("../src/images/" + value[0]["preprocessed"])} onClick= {event => displayFullScreen(idPreprocessed)}></img>
                </div>
                <div>
                    <p>Thresholded {key}</p>
                    <img class="contain" id ={idThresholded} src={require("../src/images/" + value[1]["thresholded"])}  onClick= {event => displayFullScreen(idThresholded)}></img>
                </div>
                {displaySegmented ? (
                    <div>
                        <p>Segmented {key}</p>
                        <img id ={idSegmented}  src={require("../src/images/" + value[1]["segmented"])}  onClick= {event => displayFullScreen(idSegmented)}></img>         
                    </div>                  
                ):(<></>)         
                }   
            </div>])
        }))

        //Set marker area plot
        if(area != ""){
            setAreaPlots(
                <div>
                    <p>Measurement of marker positive area</p>
                    <img id ="areaPlot" src={require("../src/images/Area/" + area)} onClick= {event => displayFullScreen("areaPlot")}></img>
                </div>
            )
        }

    } 


    const displayFullScreen = (id) => { 
        let elem = document.getElementById(id)
        if(document.fullscreenElement){
            document.exitFullscreen()
        }
        else{
            if (elem.requestFullscreen) {
                elem.requestFullscreen();
              } else if (elem.webkitRequestFullscreen) { /* Safari */
                elem.webkitRequestFullscreen();
              } else if (elem.msRequestFullscreen) { /* IE11 */
                elem.msRequestFullscreen();
              }
        }

    }

    //Handles changes in the sample drop-down list
    const handleChangeSelect = (event) => { 
        //displays images/nothing depending of the selected drop-down value
        if(event.target.value != "None"){
            document.getElementById(event.target.value).style.display = "block"
        }
        //Hides images depending of the value
        if(sample != "" && sample != "None"){
            document.getElementById(sample).style.display = "none"
        }
        sample = event.target.value      
    }

    //Display HTML to the user
    return(
        <div>
            <form id="resultsForm">
                <div hidden id="load">
                    <p>Running SIMPLI</p>         
                </div>

                <div hidden id="results">  
                    <header>SIMPLI results</header>  
                    <button id="resultsButton" type="button" onClick={event => displayResults()}>Display results</button>
                    {dropDown}
                    {sampleImages}
                    {areaPlots}
                </div>
            </form>
        </div>
    )
}
export default GetResults;
