import React,  { useState, forwardRef, useImperativeHandle } from 'react';
import axios from "axios";
import './App.css';
import { v4 as uuidv4 } from 'uuid';

import { render, waitForElementToBeRemoved } from '@testing-library/react';
//import GetSettings from "./getSettings.js"



//If no markers prevent user to go forward add new textbox
//able user input non unique markers

const GetMetadata = forwardRef((props, ref)=>{

    const [renderArea, setRenderArea] = useState(false)
    const [allMarkers, setAllMarkes]  = useState(false)
   // const [inputFields, setInputFields] = useState([]);
    const [markersDict, setMarkersDict] = useState([{}])
    const [counter, setCounter] = useState(0)
    let inputFields = []

    const [originalMarkers, setOriginalMarkers] = useState({}) //This list keeps in track about the markers that the user inputted
    const [markerTextBox, setMarkerTextBox] = useState("")
    const [errorMessageArea, setErrorMessageArea] = useState("")

    useImperativeHandle(ref, ()=>({
        getProcesses(){
            console.log("AXIOS")
            axios.get("/submitSettings")
            .then(function(response){

                //setMarkersDict(response.data[1])
                //setMarkers(response.data[2])
                setOriginalMarkers(response.data[1])
                setMarkerTextBox("")
                let markers = [response.data[1]]
                console.log(markers)
                allMarkersChecked(markers)
                //setSamples(JSON.parse(response.data[1]))
                //console.log(samples)
                //console.log(typeof(samples))
                //Parse values from response data to boolean format
                for (let prop in response.data[0]) {
                    if (response.data[0].hasOwnProperty(prop)) {
                        response.data[0][prop] = (response.data[0][prop] == 'true' || response.data[0][prop] == 'false')? response.data[0][prop] === 'true': response.data[0][prop] ;
                    }
                } 
                //Checkbox to Compute marker positive areas to all markers
                //+ input fields to custom markers
                if(response.data[0].execute_area == true){
                    setRenderArea(true)
                    //console.log("Render area ", renderArea)
                }
                else if(response.data[0].execute_area == false)setRenderArea(false)

                if(response.data[0].execute_sd_segmentation == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_cell_type_identification == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_cell_clustering == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_cell_thresholding == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_homotypic_interactions == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_heterotypic_interactions == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_permuted_interactions == true){
                    console.log("RENDER SOMETHING")
                }

                //console.log(skip_area)
                //console.log(response.data.skip_area)
            })
        },
        submitMetadata(){

            console.log("metadata")
            console.log(allMarkers)

            console.log("getMETADATA")
        }
    }))

    const allMarkersChecked = (markers) => {    
            console.log("ORIGINAL MARKERS", originalMarkers)
            setMarkersDict(originalMarkers)
            let count = -1;

            //let markers = originalMarkers //Using a temp list because the state variable doesn't update immediately 
            console.log(markers)
            setMarkerTextBox(Object.entries(markers).map(([key,index]) => {
                let obj = markers[key]
                console.log(obj)
                let marker = Object.keys(obj)[0]
                console.log(marker)
                if(marker in originalMarkers){
                    console.log("INCLUDES")
                    count = count + 1
                    setCounter(count)  

                    console.log(count)
                    return(<div class="row" id={count} value={marker}>
                        <div class="col-1">    
                            <input name="marker" value={marker} disabled size="10"/>                
                        </div>
                        <div class="col-2">    
                            <input name="mainMarker" value={marker} disabled size="10"/>                
                        </div>
                        <div class="col-3">
                            <button  onClick={() => removeMarker(count)}>Remove</button>                  
                        </div>  
                    </div>)    

                }     
            }
        ))             
    }

    const addMarkers = event =>{
        let count = counter + 1
        console.log(count)
        setMarkerTextBox([...markerTextBox,
        <div class="row" id={count}>
            <div class="col-1">    
                <input name="marker"  id={0-count} onBlur={event => handleChangeMarker(count,event)} size="10"/>                
            </div>
            <div class="col-2">    
                <input name="mainMarker" onBlur={event => handleChangeMainMarker(count,event)} size="10"/>                
            </div>
            <div class="col-3">
                <button disabled={markerTextBox.length === 0} onClick={() => removeMarker(count)}>Remove</button>        
            </div> 

        </div>
        ])  
        setCounter(count)       
    }


    const removeMarker = (id) => {

        let key = document.getElementById(id).getAttribute("value")
        delete markersDict[key]
        document.getElementById(id).remove()
        console.log(markersDict)

    }

    const handleChangeMarker = (id, event) => {
        let inputMarkers = []

        let inputString = event.target.value.replace(/\s/g, "") //Removes the whitespaces
        let marker = inputString
        console.log("INPUTSTRING", inputString)
        console.log(markersDict)
        //Checks the logical operators the user inputted
        if(marker.includes("&") || marker.includes("|") || marker.includes("!")){
            inputMarkers = marker.split(/[&!|]+/) //Makes a list of the markers
        }
        else{
            //If the input didn't contain logical operators -> there was only one marker, hence we can put it directly to a list
            inputMarkers.push(marker)
        }
        let inputError = false
        //Loop that checks the user inputted a marker that that the tiff-images have
        for(let elem in inputMarkers){
            if(inputMarkers[elem] in originalMarkers){
                inputError = false
            }
            else{
                inputError = true
                break;
            }
        }
        //If the tiff-images have the marker we can put it to the dictionary
        if(inputError === false){
            setMarkersDict({...markersDict,[inputString]:""})
            setErrorMessageArea("")
        }
        //If not, raising an error to the user
        else if(inputError === true){
            setErrorMessageArea("ERROR: input " + inputString + " couldn't be processed, please change the input")
            //alert("ERROR: input " + inputString + " couldn't be processed, please change the input format")

        }

      }

    const handleChangeMainMarker = (id,event) => {

        let mainMarker = event.target.value.replace(/\s/g, "")
        let key = document.getElementById(0-id).value
        let inputError = handleMarkerInput(mainMarker)
        //If the tiff-images have the marker we can put it to the dictionary
        if(inputError === false){
            setMarkersDict({...markersDict,[key]:mainMarker})

        }
        //If not, raising an error to the user
        else if(inputError === true){
            setErrorMessageArea("ERROR: input " + mainMarker + " couldn't be processed, please change the input")
            //alert("ERROR: input " + mainMarker + " couldn't be processed, please change the input format")
        }   
    }

    function handleMarkerInput(marker){
        let inputMarkers = []
        console.log("INPUMarker", marker)
        console.log(markersDict)
        //Checks the logical operators the user inputted
        if(marker.includes("&") || marker.includes("|") || marker.includes("!")){
            inputMarkers = marker.split(/[&!|]+/) //Makes a list of the markers
        }
        else{
            //If the input didn't contain logical operators -> there was only one marker, hence we can put it directly to a list
            inputMarkers.push(marker)
        }
        let inputError = false
        //Loop that checks the user inputted a marker that that the tiff-images have
        for(let elem in inputMarkers){
            if(inputMarkers[elem] in originalMarkers){
                inputError = false
            }
            else{
                inputError = true
                break;
            }
        }
        return inputError
    }


    console.log("COUNTER: ", counter)
    return(
        <div>
            <form class="metadataForm">
                <b>Submit metadata:</b>
                {renderArea ? (
                    <div>
                    <React.Fragment> 
                        <p>Area</p>
                        <div class="row">
                                <div class="col-1">    
                                    <p>Marker</p>
                                </div>
                                <div class="col-2">    
                                    <p>Main marker</p>

                            </div>

                            </div>
                        {markerTextBox}
                        <button type="button" onClick={addMarkers}>Add markers</button>


                    </React.Fragment>
                    </div>
                    /*
                    <div>
