import React,  { useState, forwardRef, useImperativeHandle } from 'react';
import axios from "axios";
import './App.css';


const GetResults = forwardRef((props, ref)=>{

    const [plotPaths, setPlotPaths] = useState("")
    const [areaPlots, setAreaPlots] = useState("")



    useImperativeHandle(ref, ()=>({
        displayResults(){
            console.log("OK")
            axios.get("/submitSettings")
            .then(function(response){
               console.log(response.data[0])
               let paths = response[0]
               setPlotPaths(paths)
               displayImages(paths)          

            })
        }
    }))

   const displayResults = () => {
        console.log("OK")
        axios.get("/submitSettings")
        .then(function(response){
           console.log(response.data[0])
           let paths = response[0]
           setPlotPaths(paths)
           displayImages(paths)          

        })
    }


    const displayImages = (paths) => { 
        console.log(paths)
        setAreaPlots(<img src={require(paths)}/>)
    }


    return(
    <div>

      <div hidden id="load">
          <p>Running the analysis.</p>         
      </div>

      <React.Fragment> 
      <div hidden id="results">
          <p>Display results</p>
          {areaPlots}
          {console.log("AREA PLOTS ", areaPlots)}
          <a href="http://localhost:3000">Home page</a>         
      </div>
      </React.Fragment> 


    </div>

    )
})
export default GetResults;
