import React,  { useState, forwardRef, useImperativeHandle } from 'react';
import axios from "axios";
import './App.css';

const GetResults = () => { 

    const [plotPaths, setPlotPaths] = useState("")
    const [areaPlots, setAreaPlots] = useState("")
    const [trigger, setTrigger] = useState(true)

    console.log("_____________________________________")
    if(trigger === true){
        displayResults()
        setTrigger(false)
    }

    const displayResults = () => {
        document.getElementById("resultsButton").style.display = "none"
        console.log("OK")
        axios.get("/submitSettings")
        .then(function(response){
           console.log(response.data[0])
           //let paths = "../src/images/area/total_ROI_area_boxplots.png"
           let paths = response.data[0]
           setPlotPaths(paths)
           displayImages(paths)          
        })
    }

    const displayImages = (paths) => { 
        console.log(paths)
        setAreaPlots(<img src={paths}/>)
    } 

    return(
    <div>
      <div hidden id="load">
          <p>Running the analysis.</p>         
      </div>

      <div hidden id="results">        
          {areaPlots}
          <button id="resultsButton" onChange={event => displayResults()}>Display results</button>
          <a href="http://localhost:3000">Home page</a>         
      </div>
    </div>

    )
}
export default GetResults;
