import React,  { useState, forwardRef, useImperativeHandle } from 'react';
import axios from "axios";
import './App.css';
import { v4 as uuidv4 } from 'uuid';

import { render, waitForElementToBeRemoved } from '@testing-library/react';
//import GetSettings from "./getSettings.js"


const GetMetadata = forwardRef((props, ref)=>{

    const [renderArea, setRenderArea] = useState(false)
    const [allMarkers, setAllMarkes]  = useState(false)
   // const [inputFields, setInputFields] = useState([]);
    const [samplesDict, setSamplesDict] = useState({})
    let inputFields = []
    const [markers, setMarkers] = useState([])
    const [markerTextBox, setMarkerTextBox] = useState("")

    useImperativeHandle(ref, ()=>({
        getProcesses(){
            console.log("AXIOS")
            axios.get("/submitSettings")
            .then(function(response){

                console.log(response)
                console.log(response.data[0])
                console.log(response.data[1])
                console.log(response.data[2])
                setSamplesDict(response.data[1])
                console.log(typeof(response.data[2]))
                var tempArr = []
                for(var elem in response.data[2]){
                    tempArr.push(elem)
                }
                console.log(tempArr)
                console.log(typeof(tempArr))
                setMarkers(tempArr)
                console.log(typeof(tempArr))


                //setSamples(JSON.parse(response.data[1]))
                //console.log(samples)
                //console.log(typeof(samples))
                //Parse values from response data to boolean format
                for (let prop in response.data[0]) {
                    if (response.data[0].hasOwnProperty(prop)) {
                        response.data[0][prop] = (response.data[0][prop] == 'true' || response.data[0][prop] == 'false')? response.data[0][prop] === 'true': response.data[0][prop] ;
                    }
                } 
                //Checkbox to Compute marker positive areas to all markers
                //+ input fields to custom markers
                if(response.data[0].execute_area == true){

                    console.log("RENDER SOMETHING")
                    console.log(allMarkers)
                    setRenderArea(true)
                    //console.log("Render area ", renderArea)

                }
                else if(response.data[0].execute_area == false)setRenderArea(false)

                if(response.data[0].execute_sd_segmentation == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_cell_type_identification == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_cell_clustering == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_cell_thresholding == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_homotypic_interactions == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_heterotypic_interactions == true){
                    console.log("RENDER SOMETHING")
                }
                if(response.data[0].execute_permuted_interactions == true){
                    console.log("RENDER SOMETHING")
                }

                //console.log(skip_area)
                //console.log(response.data.skip_area)
            })
        },
        submitMetadata(){

            console.log("metadata")
            console.log(allMarkers)

            console.log("getMETADATA")
        }
    }))





    const allMarkersChecked = () => {

        //inputFields = []

        //console.log("INPUTFIELDS", inputFields)

        setAllMarkes(!allMarkers)
        console.log(allMarkers)
        if(allMarkers === false){
            console.log("checked")


        //Maps the key value pairs in samplesDict
        /*Object.keys(samplesDict).forEach(function(key, index){
            inputFields.push(samplesDict[key])
            console.log(inputFields)
            console.log("READY")
        })*/
        console.log(markers)
        console.log(typeof(markers))
        console.log("ADDING THE input")
        setMarkerTextBox(markers.map((item,index) => 

        <div class="row" id={index} value={item}>
            {console.log(item)}
            {console.log(index)}

                <div class="col-1">    
                    <input name="marker" value={item} onChange={event => handleChangeInput(index,event)} size="10"/>                
                </div>
                <div class="col-2">    
                    <input name="mainMarker" value={item} onChange={event => handleChangeInput(index,event)} size="10"/>                
                </div>
                <div class="col-3">
                    <button disabled={markerTextBox.length === 1} onClick={() => removeMarker(index)}>Remove</button>

                 </div>  


            </div>
        ))
        console.log("Input added")
    }


    }

    const addMarkers = event =>{
        console.log(markerTextBox.length)
        console.log(typeof(markerTextBox))
        //THe div doesn't have a value
        //Removing this doesn't work
        setMarkerTextBox([...markerTextBox,
        <div class="row" id={markerTextBox.length+1}>
            <div class="col-1">    
                <input name="marker" onChange={event => handleChangeInput(markerTextBox.length+1,event)} size="10"/>                
            </div>
            <div class="col-2">    
                <input name="mainMarker" onChange={event => handleChangeInput(markerTextBox.length+1,event)} size="10"/>                
            </div>
            <div class="col-3">
                <button disabled={markerTextBox.length === 1} onChange={event => removeMarker(markerTextBox.length+1, event)}>Remove</button>        
            </div> 

        </div>
        ])

    }

    const removeMarker = (id) => {
        console.log("remove")
       /* console.log("REMOVE")
        let value = document.getElementById(id).getAttribute("value")

        console.log(value)
        console.log(markers)
        var index = markers.indexOf(value)
        if(index >=0){
            markers.splice(index,1)
        }

        console.log(markers)*/
        /*console.log("DELETE")
        console.log(samplesDict)
        for(var key in samplesDict){
            if(samplesDict[key] == value){
                //let k = samplesDict[value]
                console.log(key)
                delete samplesDict[key]
            }
        }

        console.log(samplesDict)
        document.getElementById(id).remove()*/


    }

    const handleChangeInput = (id, event) => {
        /**const newInputFields = inputFields.map(i => {
          if(id === i.id) {
            console.log(event.target.name)
          }      
          return i;
        })   */
        //setInputFields(newInputFields);  
      }

    return(
        <div>
            <form class="metadataForm">
                <b>Submit metadata:</b>
                {renderArea ? (

                    <div>
                        <p>Area</p>
                            <div class = "checkbox" >

                            <p>Select all markers: </p>
                                <input type = "checkbox" name="cbAreas" onChange={() => allMarkersChecked()}/>
                        </div>
                        {allMarkers ? (
                           <div>
                            <div class="row">
                                <div class="col-1">    
                                    <p>Marker</p>
                                </div>
                                <div class="col-2">    
                                    <p>Main marker</p>

                            </div>

                            </div>
                            {markerTextBox}

                            <button type="button" onClick={addMarkers}>Add markers</button>

                            </div>


                         ):(
                            <React.Fragment>

                                <div class="row">
                                <div class="col-1">    
                                    <p>Marker</p>
                                    <input name="marker"/>                
                                </div>
                                <div class="col-2">    
                                    <p>Main marker</p>
                                    <input name="main_marker"/>
                                </div>
                                </div>

                            </React.Fragment>


                         )}

                    </div>



                ):(<></>)}



            </form>
        </div>

        )

})
export default GetMetadata;
//<div>{allMarkers && <p>Loading...</p>}</div>#setAllMarkes(!allMarkers);
